mapper.select:
select resId,rescode,title,unifytype,time,imgpath,filesuffix,size,updatetime,tfcode,tf_title from ( select a.id as resId,a.resourceid as rescode,a.name as title,a.updatetime,a.createTime as time,p.id as unifyTypeId,a.assetSize as size ,p.MType as unifyType,a.AssetPath as imgPath ,REVERSE(LEFT(REVERSE(AssetPath),LOCATE('.',REVERSE(AssetPath))-1)) as fileSuffix ,c.tfcode,c.name as tf_title from z_asset a LEFT JOIN x_resourcetype p on p.id = a.unifyTypeId LEFT JOIN z_asset_syscourse s on s.assetid = a.id LEFT JOIN j_syscourse c on c.tfcode = s.tfcode where a.iscourseware = true and a.flag =false and a.FileType = 'A' and s.flag = false and a.userid = #{userId} and s.tfcode like #{tfcode}
and a.name like #{title}
)temp_result where 1 =1
ORDER BY title desc, updatetime desc,size desc
ORDER BY updatetime desc,title desc, size desc
ORDER BY size desc ,updatetime desc,title desc
ORDER BY title desc, updatetime desc,size desc

select p.id,p.code,p.mtype from x_resourcetype p where p.flag = false and p.code in ( select left(p.code,4) from x_resourcetype p where p.id in ( select z.MType from z_resdown_record o LEFT JOIN z_resource z on z.id = o.resId where o.userid = #{userId} and o.fromflag = 0
union all
select d.MType from z_resdown_record o LEFT JOIN z_districts_resource d on d.id = o.resId where o.userid = #{userId} and o.fromflag in (3,4) ));

select id,code,mtype from x_resourcetype p  where p.id in (
select DISTINCT a.unifytypeid from z_asset a  
LEFT JOIN z_asset_syscourse s on s.assetid = a.id
			where a.flag =false and a.FileType = 'A'
      and s.flag = false  
			and a.userid =#{userId}  
			and s.tfcode like #{tfcode} 
			
		
		and a.name like  #{title}
			
		
			and a.islocal = 0 
		
					
)ORDER BY code

 	select  resId,title,1 as fromflag,unifytype,time,imgpath,filesuffix,size from  (	 
			select  a.id as resId,a.resourceid as rescode,a.name as title,a.updatetime,a.createTime as time,p.id as unifyTypeId,a.assetSize  as size 
			,p.MType as  unifyType,a.AssetPath as imgPath ,REVERSE(LEFT(REVERSE(AssetPath),LOCATE('.',REVERSE(AssetPath))-1)) as fileSuffix
			,c.tfcode,c.name as tf_title 
			from  z_asset a 
			LEFT JOIN x_resourcetype p  on p.id = a.unifyTypeId
			LEFT JOIN z_asset_syscourse  s on s.assetid = a.id
			LEFT JOIN j_syscourse c on c.tfcode = s.tfcode 
			where a.flag =false and a.FileType = 'A'
      and s.flag = false  
			and a.userid =#{userId} 
			and s.tfcode like #{tfcode} 
			
			and a.name like  #{title}
				
			
      		and a.unifytypeid =#{unifyTypeId}		
			
			
			
				and a.islocal = 0 
				
 	)temp_result
