#{item}
and a.mtype in ('')
and a.mtype in
#{item}

select DISTINCT b.FileFormat from z_districts_resource a left join z_districts_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.tfcode left join j_filetypedetail b on LOWER(REVERSE(LEFT(REVERSE(a.fname),LOCATE('.',REVERSE(a.fname))))) = b.FormatCode where a.flag = 0 and d.flag = 0 and c.StructCode like CONCAT(#{tfcode},'%') and FileFormat is not null and a.fromFlag = #{fromFlag}
and a.scopeId = #{schoolId} and a.scope = 5
and a.scopeId = #{districtId} and a.scope = 4
and a.mtype in ( SELECT b.id type_id from x_resourcetype b where b.flag=0
and (b.id = #{mtype} or b.pid = #{mtype})
)

mapper.sql:
SELECT DISTINCT 0 AS iscollect, a.isfinished,a.id,a.iscourseware isdwj, a. name name,a. name title,a.assetpath,a.keyword, d.mtype type_name, a.unifyTypeId typeId, a.islocal islocal,1 beasset,b.createtime createtime, b.courseid courseid,j.fileformat fileext, if ((select count(shareid) from x_platform_share where flag = 0 and sharedtype = 1 and shareid = a.id ) >= 1,'true','false') isshare, 0 diskorder,-1 rescoursefrom,'' collectcourseid, j.ordernum, 0 quotes,0 clicks,0 downloads,'' scope,a.assetsize fsize,0 res_time FROM z_asset a LEFT JOIN X_RESCOURSE b ON a.id = b.ResId LEFT JOIN x_resourcetype d ON d.id = a.unifyTypeId LEFT JOIN j_filetypedetail j ON a.filecode = j.formatcode AND j.flag = 0 WHERE b.userid = #{userId} AND a.flag = 0 AND b.flag = 0 AND d.flag = 0 AND b.IsUpload = 0 AND a.fileType = 'A'
and b.courseId in
#{courseId}

select 2 as iscollect,0 as isfinished,a.id,a.isdwj,a.fname name,a.title title, a.fpath assetpath,a.keyword,c.type_name, a.mtype typeid,0 islocal, 0 beasset, b.createtime createtime, b.dirid courseid,j.fileformat fileext,'' isshare,k.diskorder, 0 rescoursefrom,b.dirid collectcourseid,j.ordernum,0 quotes,0 clicks,0 downloads, '' scope,a.fsize fsize,m.res_time from z_resource a left join z_rescollection b on a.id=b.resid left join z_resourcetype c on b.typeId=c.type_id left join x_resourcetype d on a.mtype = d.id left join z_res_medium m on a.id = m.res_Id and a.resCode = m.res_code left join z_res_disk k on a.resCode = k.resCode left join j_filetypedetail j on LOWER(SUBSTRING_INDEX(a.fname,'.',-1)) = SUBSTR(j.FormatCode,2) and j.flag = 0 where b.userId= #{userId} and a.flag=0 and b.flag=0 and b.fromFlag = 0 and c.flag=0 and d.flag=0
and b.dirId in
#{courseId}

SELECT 2 as iscollect,a.isfinished,a.id,a.iscourseware isdwj,a. name name, a. name title,a.assetpath assetpath,a.keyword,c.mtype type_name, a.unifytypeid as typeid,a.islocal,1 beasset,b.createtime createtime, b.dirid courseid,j.fileformat fileext,'' isshare,0 diskorder, - 1 rescoursefrom,b.dirid collectcourseid,j.ordernum,0 quotes, 0 clicks,0 downloads,'' scope,a.assetsize fsize,0 res_time FROM z_asset a LEFT JOIN z_rescollection b ON a.id = b.resid LEFT JOIN x_resourcetype c ON a.unifyTypeId = c.id LEFT JOIN j_filetypedetail j ON a.filecode = j.formatcode WHERE b.userId = #{userId} AND a.flag = 0 AND b.flag = 0 AND b.fromFlag = 1
and b.dirId in
#{courseId}

select 2 as iscollect,0 as isfinished,a.id,a.isdwj,a.fname name,a.title title, a.fullpath assetpath,a.keyword,c.mtype type_name,b.typeid,a.islocal, 0 beasset, b.createtime createtime, b.dirid courseid,j.fileformat as fileext, '' isshare,0 diskorder, a.fromflag rescoursefrom,b.dirid collectcourseid,j.ordernum, 0 quotes,0 clicks,0 downloads,'' scope ,a.fsize fsize,0 res_time from z_districts_resource a left join z_rescollection b on a.id=b.resid left join x_resourcetype c on b.typeId=c.id left join j_filetypedetail j on LOWER(SUBSTRING_INDEX(a.fname,'.',-1)) = SUBSTR(j.FormatCode,2) and j.flag = 0 where b.userId= #{userId} and a.flag=0 and b.flag=0 and b.fromFlag in (3,4) and c.flag=0
and b.dirId in
#{courseId}

select DISTINCT 2 as iscollect, a.isfinished,a.id,a.iscourseware isdwj,a.name name, a.name title,a.assetpath,d.mtype type_name,d.id typeid,a.keyword,a.islocal islocal, 1 beasset, b.createtime createtime,b.courseid courseid,j.fileformat fileext, if((select count(shareid) from x_platform_share where flag =0 and sharedtype =1 and shareid = a.id)>=1,'true','false')isshare, 0 diskorder,6 rescoursefrom,'' collectcourseid,j.ordernum ,p.quotes,p.clicks, p.downloads,p.scope,a.assetsize fsize,0 res_time from z_asset a left join X_RESCOURSE b on a.id=b.ResId LEFT JOIN x_platform_share p on a.id = p.shareId LEFT JOIN x_resourcetype d ON a.unifyTypeId = d.id left join j_filetypedetail j on a.filecode = j.formatcode and j.flag = 0 where b.userid = #{userId} and a.flag = 0 and b.flag=0 and d.flag = 0 and p.flag=0 and b.IsUpload=0 and p.sharedType=1
and b.courseId in
#{id}

