mapper.select:
SELECT id from ( SELECT b.id,b.pid,a.poolId from z_res_pooltype a LEFT JOIN x_resourcetype b on a.resTypeId = b.ID where a.flag=0 and b.flag=0 UNION SELECT a.id,a.pid,b.poolId from x_resourcetype a LEFT JOIN z_res_pooltype b on a.Pid = b.resTypeId where a.flag=0 and b.flag=0) temp WHERE 1 = 1
AND poolId = #{poolId}
AND poolId NOT IN
#{id}
AND id not in (37,38,39)
AND id = #{typeId} or pid = #{typeId}

select case when b.PId=0 then b.MType ELSE pb.MType END mtype, case when b.PId=0 then b.id ELSE pb.id END id,count(b.MType) mtypenum from z_resource a left join X_RESOURCETYPE b on a.MType = b.id left join X_RESOURCETYPE pb on pb.Id=b.pid LEFT JOIN z_resnav c ON a.ResCode = c.ResCode
select b.mtype,b.id,count(b.MType) mtypenum from z_resource a LEFT JOIN z_resnav c ON a.ResCode = c.ResCode left join x_resourcetype b on a.MType = b.id and b.level = 2
where a.flag = 0 and b.flag = 0 AND c.flag = 0
AND c.StructCode in
#{code}
AND b.id IN
#{id}
AND a.fromflag IN
#{id}

SELECT fileformat ,count(fileformat) count from z_resource a right join z_resnav v on v.rescode = a.rescode left join j_filetypedetail b on fileExt = b.FormatCode LEFT JOIN x_resourcetype r on r.id = a.mtype WHERE 1=1
and v.structcode in
#{code}
AND r.id IN
#{id}
AND a.fromflag IN
#{id}
and a.flag = 0 and b.flag = 0 group by fileformat

SELECT mtype,id,count(1) as mtypenum from ( select case when b.PId=0 then b.MType ELSE pb.MType END MType,case when b.PId=0 then b.id ELSE pb.id END id,b.DisplayIndex from z_districts_resource a left join x_resourcetype b on a.MType = b.id left join x_resourcetype pb on pb.Id=b.pid LEFT JOIN z_districts_resnav n on n.ResCode = a.ResCode where 1 =1
and n.structcode in
#{code}
AND a.fromFlag = 3 AND a.scope = 5 and a.ScopeId = ${schoolId}
AND a.fromFlag = 4 AND a.scope = 4 and a.ScopeId = ${districtId}
and a.flag = 0 and n.Flag = false and b.flag = 0 and (a.state=3 or a.state=6) and (pb.flag = 0 or pb.flag is null) )temm group by MType order by displayIndex asc

SELECT FileFormat as formatcode, count(FileFormat) count
FROM z_districts_resource a RIGHT JOIN j_filetypedetail b ON ( LOWER(fileExt) = b.FormatCode OR CONCAT('.', LOWER(fileExt)) = b.FormatCode ) LEFT JOIN z_districts_resnav n on n.ResCode = a.ResCode
WHERE 1 = 1
and n.structcode in
#{code}
AND a.fromFlag = 3 AND a.scope = 5 and a.ScopeId = ${schoolId}
AND a.fromFlag = 4 AND a.scope = 4 and a.ScopeId = ${districtId}
AND a.flag = 0 AND (a.state = 3 OR a.state = 6) AND b.flag = 0 group by FileFormat
