select mtype,id from ( select distinct case when b.PId=0 then b.MType ELSE pb.MType END mtype,case when b.PId=0 then b.id ELSE pb.id END id from z_districts_resource a LEFT JOIN z_districts_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.tfcode left join x_resourcetype b on a.MType = b.id left join x_resourcetype pb on pb.Id = b.pid where a.flag = 0 and c.flag=0 and d.flag = 0 and c.StructCode like CONCAT(#{tfcode},'%')
