mapper.select:
SELECT id, title, (CASE WHEN updatetime > DATE_SUB(CURDATE(), INTERVAL 1 WEEK) then 'withinweek' WHEN updatetime > DATE_SUB(CURDATE(), INTERVAL 1 MONTH) then 'withinmonth' ELSE 'moreearly' END) as timeLabel FROM j_lession_prepare WHERE tfcode LIKE #{tfcode} AND userid = #{userId} AND flag = false
AND title like #{title}
ORDER BY updatetime desc,createtime desc

select id,title,resId,fromFlag,unifytype,size,timeline,time,imgpath,filesuffix from ( select t.id ,a.id as resId,a.name as title,a.createTime as time,1 as fromFlag,p.id as unifyTypeId ,p.MType as unifyType,a.AssetSize as size,'' as timeline,a.AssetPath as imgPath ,'' as fileSuffix,t.orderIdx from j_prepare_content t LEFT JOIN z_asset a on a.id = t.contId LEFT JOIN x_resourcetype p on p.id = a.unifyTypeId where t.preid = #{prepareId} and t.flag = false and t.contType in (1,9) and a.flag =false and a.FileType = 'A'
UNION ALL
select t.id,z.Id as resId,z.title,z.CreateDT as time,0 as fromFlag,p.id as unifyTypeId ,p.MType as unifyType,z.FSize as size,IFNULL(m.res_time,'') as timeline ,CONCAT(z.FPath,'/',z.Fname) as imgPath ,'' as fileSuffix,t.orderIdx from j_prepare_content t LEFT JOIN z_resource z on t.contId = z.Id LEFT JOIN x_resourcetype p on p.id = z.MType LEFT JOIN z_res_medium m on m.res_code = z.ResCode where t.preid = #{prepareId} and t.flag = false and t.contType in (2,10) and z.flag = false
UNION ALL
select t.id,r.id as resId,r.title,r.CreateDT as time ,fromFlag as fromFlag,p.id as unifyTypeId ,p.MType as unifyType,r.FSize as size,'' as timeline,r.fullpath as imgPath ,'' as fileSuffix,t.orderIdx from j_prepare_content t LEFT JOIN z_districts_resource r on r.id = t.contId LEFT JOIN x_resourcetype p on p.id = r.mtype where t.preid = #{prepareId} and t.flag = false and t.contType in (11,12) and r.flag = false )temp_result where temp_result.unifyTypeId NOT IN ( select id from x_resourcetype where ( id in #{item}
OR pid in #{item}
) ) ORDER BY orderIdx desc

select id,title,resId,fromFlag,unifytype,size,timeline,time,imgpath,filesuffix from ( select t.id ,a.id as resId,a.name as title,a.createTime as time,1 as fromFlag,p.id as unifyTypeId ,p.MType as unifyType,a.AssetSize as size,'' as timeline,a.AssetPath as imgPath ,'' as fileSuffix,t.orderIdx from j_prepare_content t LEFT JOIN z_asset a on a.id = t.contId LEFT JOIN x_resourcetype p on p.id = a.unifyTypeId where t.preid = #{prepareId} and t.flag = false and t.contType in (1,9) and a.flag =false and a.FileType = 'A' and a.islocal = 0
UNION ALL
select t.id,z.Id as resId,z.title,z.CreateDT as time,0 as fromFlag,p.id as unifyTypeId ,p.MType as unifyType,z.FSize as size,IFNULL(m.res_time,'') as timeline ,CONCAT(z.FPath,'/',z.Fname) as imgPath ,'' as fileSuffix,t.orderIdx from j_prepare_content t LEFT JOIN z_resource z on t.contId = z.Id LEFT JOIN x_resourcetype p on p.id = z.MType LEFT JOIN z_res_medium m on m.res_code = z.ResCode where t.preid = #{prepareId} and t.flag = false and t.contType in (2,10) and z.flag = false and z.islocal = 1
UNION ALL
select t.id,r.id as resId,r.title,r.CreateDT as time ,fromFlag as fromFlag,p.id as unifyTypeId ,p.MType as unifyType,r.FSize as size,'' as timeline,r.fullpath as imgPath ,'' as fileSuffix,t.orderIdx from j_prepare_content t LEFT JOIN z_districts_resource r on r.id = t.contId LEFT JOIN x_resourcetype p on p.id = r.mtype where t.preid = #{prepareId} and t.flag = false and t.contType in (11,12) and r.flag = false and r.islocal = 1 )temp_result where temp_result.unifyTypeId NOT IN ( select id from x_resourcetype where ( id in #{item}
OR pid in #{item}
) ) ORDER BY orderIdx desc

select id as resid,title, rescode,fromflag,!islocal as  isnet,isdwj as isdwj,fullpath as path,0 as isebook 
,'' as fileext ,mtype as mtypeid
from z_districts_resource  where flag = false and id = #{0}


select  rowno from (
select   (@rowNO := @rowNo+1) AS rowno,a.tfcode,a.id from (
select tfcode,id  from  j_lession_prepare where tfcode =#{tfcode}  ORDER BY  updatetime desc 
) a,(select @rowNO :=0) b 
)temp
 where temp.id =#{prepareId}
