SELECT DISTINCT FileFormat from z_resource a left join j_filetypedetail b on (REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))) = b.FormatCode and b.flag = 0 WHERE 1=1 and a.flag = 0 and a.fromflag in #{item}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
and FileFormat is not null

    SELECT DISTINCT FileFormat from z_districts_resource a
	left join j_filetypedetail b on (REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))) = b.FormatCode and b.flag = 0 
	WHERE 1=1 and a.flag = 0 and fromFlag = #{fromFlag}  


         and a.scopeId = #{schoolId}


         and a.scopeId = #{districtId}



        and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))

    and FileFormat is not null

select distinct FileFormat from ( SELECT DISTINCT FileFormat from z_resource a left join j_filetypedetail b on (REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))) = b.FormatCode and b.flag = 0 WHERE 1=1 and a.flag = 0 and a.fromflag in #{item}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
union all
