and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
union all
SELECT DISTINCT FileFormat from z_districts_resource a left join j_filetypedetail b on (REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))) = b.FormatCode and b.flag = 0 WHERE 1=1 and a.flag = 0 and fromFlag = 4 and a.scopeId = #{districtId}
