mapper.select:
select distinct a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'\\',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId, a.FSize FSize,a.ClickTimes,a.DloadTimes,a.speaker,
m.res_time,a.UpdateDT,a.FileExt,a.resolution,1 diskorder,a.isDWJ,jf.orderNum,b.DisplayIndex, a.fromFlag, (SELECT ((IFNULL(sum(t.Ascore),0)+a.displayLevel)/(count(t.assetId)+1)) from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 1) avgScore from z_resource a left join x_resourcetype b on a.MType = b.id and b.flag = 0 left join j_filetypedetail jf on a.FileExt=jf.FormatCode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code where a.flag = 0
and a.fromflag in #{item}
and jf.FileFormat = #{format}
and (a.title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
order by avgScore desc


    select DISTINCT a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'\\',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId,a.Fsize Fsize,a.ClickTimes,a.DLoadTimes,a.speaker, 
    m.res_time,a.UpdateDT,a.FileExt,a.resolution,a.isDWJ,jf.orderNum,a.isLocal,a.ScopeId,a.state,a.fromFlag,1 diskorder,
	(SELECT ((IFNULL(sum(t.Ascore),0)+a.displayLevel)/(count(t.assetId)+1)) from  z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = #{fromFlag}) avgScore 
	from z_districts_resource a 
    left join x_resourcetype b on a.MType = b.id and b.flag = 0 
	left join j_filetypedetail jf on a.FileExt=jf.FormatCode
	left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code 
	where a.flag = 0 and fromFlag = #{fromFlag} 

         and jf.FileFormat = #{format}



         and a.scopeId = #{schoolId}


         and a.scopeId = #{districtId}



         and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))

    order by avgScore desc

select distinct a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'\\',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId, a.FSize FSize,a.ClickTimes,a.DloadTimes,a.speaker, m.res_time,a.UpdateDT,a.FileExt,a.resolution,a.isDWJ,jf.orderNum,1 isLocal,0 ScopeId,1 state,b.DisplayIndex,1 diskorder, a.fromFlag, (SELECT ((IFNULL(sum(t.Ascore),0)+a.displayLevel)/(count(t.assetId)+1)) from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 1) avgScore from z_resource a left join x_resourcetype b on a.MType = b.id and b.flag = 0 left join j_filetypedetail jf on a.FileExt=jf.FormatCode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code where a.flag = 0
and a.fromflag in (0,1,2)
and jf.FileFormat = #{format}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
union ALL
select DISTINCT a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'\\',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId,a.Fsize Fsize,a.ClickTimes,a.DLoadTimes,a.speaker, m.res_time,a.UpdateDT,a.FileExt,a.resolution,a.isDWJ,jf.orderNum,a.isLocal,a.ScopeId,a.state,0 DisplayIndex,1 diskorder,a.fromFlag, (SELECT ((IFNULL(sum(t.Ascore),0)+a.displayLevel)/(count(t.assetId)+1)) from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 3) avgScore from z_districts_resource a left join x_resourcetype b on a.MType = b.id and b.flag = 0 left join j_filetypedetail jf on a.FileExt=jf.FormatCode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code where a.flag = 0 and fromFlag = 3 and a.scopeId = #{schoolId}
and jf.FileFormat = #{format}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
union ALL
select DISTINCT a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'\\',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId,a.Fsize Fsize,a.ClickTimes,a.DLoadTimes,a.speaker, m.res_time,a.UpdateDT,a.FileExt,a.resolution,a.isDWJ,jf.orderNum,a.isLocal,a.ScopeId,a.state,0 DisplayIndex,1 diskorder,a.fromFlag, (SELECT ((IFNULL(sum(t.Ascore),0)+a.displayLevel)/(count(t.assetId)+1)) from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 4) avgScore from z_districts_resource a left join x_resourcetype b on a.MType = b.id and b.flag = 0 left join j_filetypedetail jf on a.FileExt=jf.FormatCode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code where a.flag = 0 and fromFlag = 4 and a.scopeId = #{districtId}
and jf.FileFormat = #{format}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
order by avgScore desc

    SELECT DISTINCT FileFormat from z_districts_resource a
	left join j_filetypedetail b on a.fileExt = b.FormatCode and b.flag = 0 
	WHERE 1=1 and a.flag = 0 and fromFlag = #{fromFlag}  


         and a.scopeId = #{schoolId}


         and a.scopeId = #{districtId}



        and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))

SELECT DISTINCT FileFormat from z_resource a left join j_filetypedetail b on a.fileExt = b.FormatCode and b.flag = 0 WHERE 1=1 and a.flag = 0 and a.fromflag in #{item}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
union all
SELECT DISTINCT FileFormat from z_districts_resource a left join j_filetypedetail b on a.fileExt = b.FormatCode and b.flag = 0 WHERE 1=1 and a.flag = 0 and fromFlag = 3 and a.scopeId = #{schoolId}
and (a.Title like CONCAT('%',#{searchKeyword},'%') or a.keyWord like CONCAT('%',#{searchKeyword},'%') or a.Des like CONCAT('%',#{searchKeyword},'%'))
union all
