mapper.select:
select DISTINCT a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'/',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId,a.Fsize Fsize,a.ClickTimes,a.DLoadTimes,a.speaker, m.res_time,a.UpdateDT, REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.resolution,a.isDWJ,jf.orderNum,a.isLocal,a.ScopeId,a.state,a.fromFlag, (select case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = #{fromFlag}) avgScore ,a.authorfromflag,a.authorid from z_districts_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode and jf.flag = 0 LEFT JOIN z_districts_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.tfcode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code
where a.flag = 0 and c.flag=0 and d.flag=0 and c.StructCode like CONCAT(#{tfcode},'%')
and a.State = 6
and jf.FileFormat = #{fileFormat}
and a.mtype in ( SELECT b.id type_id from x_resourcetype b where b.flag=0
and (b.id = #{mtype} or b.pid = #{mtype})
)
and a.scopeId = #{schoolId} and a.scope = 5 and a.fromFlag = #{fromFlag}
and a.scopeId = #{districtId} and a.scope = 4 and a.fromFlag = #{fromFlag}
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by DloadTimes desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by UpdateDT desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id

select id,Title,fromFlag,fullPath,thumbnailpath,FileExt,UpdateDT,DloadTimes,avgScore from (select DISTINCT a.id,a.Title,a.fromFlag,CONCAT(a.Fpath,'/',a.Fname) fullPath,a.thumbnailpath, REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.UpdateDT,a.DloadTimes, (select case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = #{fromFlag}) avgScore from z_districts_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode and jf.flag = 0 LEFT JOIN z_districts_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.tfcode where a.flag = 0 and c.flag=0 and d.flag=0 and a.id != #{resId} and c.StructCode like CONCAT(#{tfcode},'%') 11 and a.State = 6 and b.id in #{item}
and jf.FileFormat = #{fileFormat}
and a.scopeId = #{schoolId} and a.scope = 5 and a.fromFlag = #{fromFlag}
and a.scopeId = #{districtId} and a.scope = 4 and a.fromFlag = #{fromFlag}
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by DloadTimes desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by UpdateDT desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
)tmp

select DISTINCT a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'/',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId,a.Fsize Fsize,a.ClickTimes,a.DLoadTimes,a.speaker, m.res_time,a.UpdateDT, REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.resolution,a.isDWJ,jf.orderNum,a.isLocal,a.ScopeId,a.state,a.fromFlag, (select case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = #{fromFlag}) avgScore ,a.authorfromflag,a.authorid from z_districts_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode and jf.flag = 0 LEFT JOIN z_districts_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.tfcode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code
where a.flag = 0 and d.flag=0 and c.flag=0 and c.StructCode like CONCAT(#{tfcode},'%') and a.isLocal = 1
and a.Title like CONCAT('%',#{searchWord},'%')
and jf.FileFormat = #{fileFormat}
and b.id in #{item}
and a.scopeId = #{schoolId} and a.scope = 5 and a.fromFlag = #{fromFlag}
and a.scopeId = #{districtId} and a.scope = 4 and a.fromFlag = #{fromFlag}
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by DloadTimes desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by UpdateDT desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
