mapper.select:
select distinct a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'/',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId, a.FSize FSize,a.ClickTimes,a.DloadTimes,a.speaker, m.res_time,a.UpdateDT,REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.resolution,1 diskorder,a.isDWJ,jf.orderNum,b.DisplayIndex, 0 as fromFlag, (SELECT case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 1) avgScore from z_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode and jf.flag = 0 left join z_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.TFcode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code LEFT JOIN z_res_disk k on k.rescode = a.rescode where a.flag = 0 and c.flag=0 and d.flag=0 and a.fromflag in
#{item}
and d.typeFlag in (0,1) and d.tfcode like CONCAT(#{tfcode},'%')
and jf.FileFormat = #{fileFormat}
and a.mtype in #{item}
and a.mtype in ('')
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,id
order by DloadTimes desc,b.DisplayIndex asc,jf.orderNum asc,id
order by UpdateDT desc,b.DisplayIndex asc,jf.orderNum asc,id

select distinct a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'/',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId, a.FSize FSize,a.ClickTimes,a.DloadTimes,a.speaker, m.res_time,a.UpdateDT,REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.resolution,1 diskorder,a.isDWJ,jf.orderNum,b.DisplayIndex, 0 as fromFlag, (SELECT case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 1) avgScore from z_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode and jf.flag = 0 left join z_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.TFcode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code LEFT JOIN z_res_disk k on k.rescode = a.rescode where a.flag = 0 and c.flag=0 and d.flag=0 and a.fromflag in
#{item}
and d.typeFlag in (0,1) and d.tfcode like CONCAT(#{tfcode},'%')
and jf.FileFormat = #{fileFormat}
and a.Title like CONCAT('%',#{searchWord},'%')
and a.mtype in #{item}
and a.mtype in ('')
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,id
order by DloadTimes desc,b.DisplayIndex asc,jf.orderNum asc,id
order by UpdateDT desc,b.DisplayIndex asc,jf.orderNum asc,id
