mapper.select:
select distinct a.Id,a.ResCode,a.Title,a.Fname,a.Fpath,CONCAT(a.Fpath,'/',a.Fname) fullpath,a.thumbnailpath,b.MType MType, b.id typeId, a.FSize FSize ,p.clicktimes as ClickTimes,p.downloadTimes as DloadTimes,a.speaker, m.res_time,a.UpdateDT,REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.resolution,1 diskorder,a.isDWJ,jf.orderNum,b.DisplayIndex, 0 as fromFlag, (SELECT case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 1) avgScore from z_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode and jf.flag = 0 left join z_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.TFcode left join z_res_medium m on a.id = m.res_Id and a.Rescode = m.res_code LEFT JOIN z_res_disk k on k.rescode = a.rescode LEFT JOIN z_resource_operate_times p on a.rescode = p.rescode where a.flag = 0 and c.flag=0 and d.flag=0 and a.fromflag in
#{item}
and d.typeFlag in (0,1) and d.tfcode like CONCAT(#{tfcode},'%')
and jf.FileFormat = #{fileFormat}
and a.mtype in
#{item}
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by DloadTimes desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by UpdateDT desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id

select id,Title,fromFlag,fullPath,thumbnailpath,FileExt,UpdateDT,DloadTimes,avgScore from ( select distinct a.id,a.Title,0 as fromFlag,CONCAT(a.Fpath,'/',a.Fname) fullPath,a.thumbnailpath,REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname)))) as FileExt,a.UpdateDT,p.downloadtimes as DloadTimes, (SELECT case when COUNT(t.assetId) = 0 then a.displayLevel else (sum(t.Ascore) / COUNT(t.assetId) + a.displayLevel) / 2 end avgScore from z_assetevaluate t where t.assetId = a.id and t.flag = 0 and t.IsScore = 0 and t.aType = 1) avgScore from z_resource a left join x_resourcetype b on a.MType = b.id left join j_filetypedetail jf on REVERSE(LEFT(REVERSE(a.Fname),LOCATE('.',REVERSE(a.Fname))))=jf.FormatCode left join z_resnav c on a.ResCode=c.ResCode left join j_syscourse d on c.StructCode=d.TFcode LEFT JOIN z_resource_operate_times p on a.rescode = p.rescode
where a.flag = 0 and a.id != #{resId} and c.flag=0 and d.flag=0 and a.fromflag in
#{item}
and d.typeFlag in (0,1) and d.tfcode like CONCAT(#{tfcode},'%')
and jf.FileFormat = #{fileFormat}
and a.mtype in
#{item}
order by avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by DloadTimes desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
order by UpdateDT desc,avgScore desc,b.DisplayIndex asc,jf.orderNum asc,a.id
)tmp
